import CustomNavbar from "../components/Navbar";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import Carousel from "react-bootstrap/Carousel";
import { Container, Button, Card, Accordion } from "react-bootstrap";

const Home = () => {
  const [role, setRole] = useState(localStorage.getItem("role"));
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("");
  const [selectedEventType, setSelectedEventType] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    setRole(localStorage.getItem("role"));
    setToken(localStorage.getItem("token"));
  }, []);

  const handleSearch = () => {
    if (!token) {
      navigate("/login");
    } else {
      navigate(`/event-spaces?city=${selectedCity}&location=${selectedLocation}&eventType=${selectedEventType}`);
    }
  };

  // Separate Carousel Images
  const carouselImages = [
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQaJ7BNOJWElKtCdbRHEElhqiVbm_5zIMdJpQ&s",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRTKtE4Q76v0thPcrQcQAXigk4gRS9ork-DDQ&s",
  ];

  // Event Space Cards with Different Images
  const eventSpaces = [
    {
      type: "Banquet Hall",
      image: "data:image/jpeg;base64,", // Different image for cards
      description: "A spacious banquet hall for weddings and parties.",
    },
    {
      type: "Conference Room",
      image: "data:image/jpeg;base64,", // Different image for cards
      description: "Ideal for corporate meetings and presentations.",
    },
    {
      type: "Auditorium",
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTe6mldwGwWx6WXyTXLCpDVcyNpY5jtOzouoA&s", // Different image for cards
      description: "Perfect for seminars, performances, and large gatherings.",
    },
    {
      type: "Open Garden",
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT-0e5DsbT8c4OMAmGC9_TRq2wNQ9HNIK8AGA&s", // Different image for cards
      description: "A beautiful open garden venue for outdoor events.",
    },
  ];

  return (
    <div>
      {/* Search Section */}
      <Container className="mt-4">
        <div className="row mb-4">
          {/* City Dropdown */}
          <div className="col-md-4">
            <select className="form-control" value={selectedCity} onChange={(e) => setSelectedCity(e.target.value)}>
              <option value="">Select City</option>
              <option value="Vijayawada">Vijayawada</option>
              <option value="Hyderabad">Hyderabad</option>
              <option value="Vizag">Vizag</option>
            </select>
          </div>

          {/* Location Dropdown */}
          <div className="col-md-4">
            <select
              className="form-control"
              value={selectedLocation}
              onChange={(e) => setSelectedLocation(e.target.value)}
              disabled={!selectedCity}
            >
              <option value="">Select Location</option>
              {selectedCity === "Vijayawada" && (
                <>
                  <option value="Labbipet">Labbipet</option>
                  <option value="Patamata">Patamata</option>
                  <option value="Mogalrajpuram">Mogalrajpuram</option>
                </>
              )}
              {selectedCity === "Hyderabad" && (
                <>
                  <option value="L.B.Nagar">L.B.Nagar</option>
                  <option value="Jubilee Hills">Jubilee Hills</option>
                  <option value="Banjara Hills">Banjara Hills</option>
                </>
              )}
              {selectedCity === "Vizag" && (
                <>
                  <option value="Bhemilli">Bhemilli</option>
                  <option value="Seethammadhara">Seethammadhara</option>
                  <option value="Rushikonda">Rushikonda</option>
                </>
              )}
            </select>
          </div>

          {/* Event Space Type Dropdown */}
          <div className="col-md-4">
            <select className="form-control" value={selectedEventType} onChange={(e) => setSelectedEventType(e.target.value)}>
              <option value="">Select Event Space Type</option>
              <option value="Banquet Hall">Banquet Hall</option>
              <option value="Conference Room">Conference Room</option>
              <option value="Auditorium">Auditorium</option>
              <option value="Open Garden">Open Garden</option>
            </select>
          </div>

          {/* Search Button */}
          <div className="col-md-12 mt-3 text-center">
            <Button variant="primary" onClick={handleSearch} disabled={!selectedCity || !selectedLocation || !selectedEventType}>
              Search
            </Button>
          </div>
        </div>
      </Container>

      {/* Carousel with Different Images */}
      <Container className="mt-4">
        <Carousel>
          {carouselImages.map((img, index) => (
            <Carousel.Item key={index}>
              <img className="d-block w-100 img-fluid" src={img} alt={`Carousel ${index + 1}`} style={{ objectFit: "cover", height: "400px" }} />
            </Carousel.Item>
          ))}
        </Carousel>
      </Container>

      {/* Event Space Cards with Unique Images */}
      <Container className="mt-4 d-flex flex-wrap justify-content-center">
        {eventSpaces.map((space, index) => (
          <Card className="m-2 shadow" style={{ width: "18rem" }} key={index}>
            <div style={{ width: "100%", height: "200px", overflow: "hidden" }}>
              <Card.Img
                variant="top"
                src={space.image}
                className="img-fluid"
                style={{ objectFit: "cover", width: "100%", height: "100%" }}
              />
            </div>
            <Card.Body>
              <Card.Title>{space.type}</Card.Title>
              <Card.Text>{space.description}</Card.Text>
            </Card.Body>
          </Card>
        ))}
      </Container>

      {/* FAQ Section */}
      <Container className="mt-4">
        <h3>Frequently Asked Questions</h3>
        <Accordion>
          <Accordion.Item eventKey="0">
            <Accordion.Header>How do I book an event space?</Accordion.Header>
            <Accordion.Body>Simply select a city, location, and event space type, then click search.</Accordion.Body>
          </Accordion.Item>
          <Accordion.Item eventKey="1">
            <Accordion.Header>Can I add my own event space?</Accordion.Header>
            <Accordion.Body>Yes! Register as an Event Space Owner and list your venue.</Accordion.Body>
          </Accordion.Item>
        </Accordion>
      </Container>
    </div>
  );
};

export default Home;
